export const TYPE_ABBREVIATIONS: Record<string, string> = {
  STEP: 'STP',
  STRING: 'STR',
  NUMBER: 'NUM',
  BOOLEAN: 'BOOL',
  OBJECT: 'OBJ',
  ARRAY: 'ARR',
  FUNCTION: 'FN',
  ANY: 'ANY',
};

